INSERT INTO books (bookID, bookName, bookCounts, detail, bookType, Press, bookAuthor, bookDate, bookPage) VALUES (26, '明朝那些事儿（1-9）', 1001, '《明朝那些事儿》讲述从1344年到1644年，明朝三百年间的历史。作品以史料为基础，以年代和具体人物为主线，运用小说的笔法，对明朝十七帝和其他王公权贵和小人物的命运进行全景展示，尤其对官场政治、战争、帝王心术着墨最多。作品也是一部明朝政治经济制度、人伦道德的演义。', '历史', '中国海关出版社', '当年明月', '2009-04-12', 2532);
